Database.addText("de.candyBoxRequestStatusBarUnlockedCfg", "Um eine weitere bitten (5 Süßigkeiten)");
Database.addText("de.candyBoxRequestStatusBarUnlockedSave", "Noch einmal bitten (5 Süßigkeiten)");
